descriptor.controllerScript:
self.onInit = function() {
    var i, gpio;

    var scope = self.ctx.$scope;
    var settings = self.ctx.settings;

    scope.gpioList = [];
    scope.gpioByPin = {};
    for (var g = 0; g < settings.gpioList.length; g++) {
        gpio = settings.gpioList[g];
        scope.gpioList.push(
            {
                row: gpio.row,
                col: gpio.col,
                pin: gpio.pin,
                label: gpio.label,
                enabled: false,
                colorOn: tinycolor(gpio.color).lighten(20).toHexString(),
                colorOff: tinycolor(gpio.color).darken().toHexString()
            }
        );
        scope.gpioByPin[gpio.pin] = scope.gpioList[scope.gpioList.length-1];
    }

    scope.ledPanelBackgroundColor = settings.ledPanelBackgroundColor || tinycolor('green').lighten(2).toRgbString();

    scope.gpioCells = {};
    var rowCount = 0;
    for (i = 0; i < scope.gpioList.length; i++) {
        gpio = scope.gpioList[i];
        scope.gpioCells[gpio.row+'_'+gpio.col] = gpio;
        rowCount = Math.max(rowCount, gpio.row+1);
    }

    scope.prefferedRowHeight = 32;
    scope.rows = [];
    for (i = 0; i < rowCount; i++) {
        var row = [];
        for (var c =0; c<2;c++) {
            if (scope.gpioCells[i+'_'+c]) {
                row[c] = scope.gpioCells[i+'_'+c];
            } else {
                row[c] = null;
            }
        }
        scope.rows.push(row);
    }    

    self.onResize();
}

self.onDataUpdated = function() {
    var changed = false;
    for (var d = 0; d < self.ctx.data.length; d++) {
        var cellData = self.ctx.data[d];
        var dataKey = cellData.dataKey;
        var gpio = self.ctx.$scope.gpioByPin[dataKey.label];
        if (gpio) {
            var enabled = false;
            if (cellData.data.length > 0) {
                var tvPair = cellData.data[cellData.data.length - 1];
                enabled = (tvPair[1] === true || tvPair[1] === 'true');
            }
            if (gpio.enabled != enabled) {
                changed = true;
                gpio.enabled = enabled;
            }
        }
    }
    if (changed) {
        self.ctx.$scope.$digest();
    }    
}

self.onResize = function() {
    var rowCount = self.ctx.$scope.rows.length;
    var prefferedRowHeight = (self.ctx.height - 35)/rowCount;
    prefferedRowHeight = Math.min(32, prefferedRowHeight);
    prefferedRowHeight = Math.max(12, prefferedRowHeight);
    self.ctx.$scope.prefferedRowHeight = prefferedRowHeight;

    var ratio = prefferedRowHeight/32;

    var leftLabels = $('.gpio-left-label', self.ctx.$container);
    leftLabels.css('font-size', 16*ratio+'px');
    var rightLabels = $('.gpio-right-label', self.ctx.$container);
    rightLabels.css('font-size', 16*ratio+'px');
    var pins = $('.pin', self.ctx.$container);
    var pinsFontSize = Math.max(9, 12*ratio);
    pins.css('font-size', pinsFontSize+'px');    
}

self.onDestroy = function() {
}

